NetworkSource.StatementList:
LAR1 P##plc_commands //Command from program
// copy all commands to the bools
A DIX [AR1,P#3.0] // WinCC tag bit 0 = plc_start
A DIX [AR1,P#3.1] // WinCC tag bit 1 = plc_stop
A DIX [AR1,P#3.2] // WinCC tag bit 2 = plc_hold
A DIX [AR1,P#3.3] // WinCC tag bit 3 = plc_restart
A DIX [AR1,P#3.4] // WinCC tag bit 4 = plc_abort
A DIX [AR1,P#3.5] // WinCC tag bit 5 = plc_reset
A DIX [AR1,P#3.6] // WinCC tag bit 6 = plc_pause
A DIX [AR1,P#3.7] // WinCC tag bit 7 = plc_resume
A DIX [AR1,P#2.0] // WinCC tag bit 8 = plc_auto
A DIX [AR1,P#2.1] // WinCC tag bit 9 = plc_man
//A DIX [AR1,P#2.2] // WinCC tag bit 10 //= plc_BTN1
//A DIX [AR1,P#2.3] // WinCC tag bit 11 //= plc_start
//A DIX [AR1,P#2.4] // WinCC tag bit 12 //= plc_start
// reset operator command L DW#16#F000 T #hmi_commands T #plc_commands

// check if stepnumber == previous_stepnumber L stepnumber L previous_stepnumber ==I // if condition true then { JCN TIM1
// increase timer L scantime L steptime_s +R T steptime_s
JU TIM2 // } else { TIM1: NOP 0
// reset timer L 0.0 T steptime_s
TIM2: NOP 0 // end if
// calculate the other two timers L steptime_s L 60.0 /R T steptime_m
L 60.0 /R T steptime_h

//-------------------------------- // timer_dosing // //--------------------------------
AN timer_dosing_pause // if not paused ... JCN TI1
L scantime // increase the timer L timer_dosing_s +R T timer_dosing_s
TI1: NOP 0 // end if
// calculate the other two timers L timer_dosing_s L 60.0 /R T timer_dosing_m
L 60.0 /R T timer_dosing_h
//-------------------------------- // timer_mixing // //--------------------------------
AN timer_mixing_pause // if not paused ... JCN TI2
L scantime // increase the timer L timer_mixing_s +R T timer_mixing_s
TI2: NOP 0 // end if
// calculate the other two timers L timer_mixing_s L 60.0 /R T timer_mixing_m
L 60.0 /R T timer_mixing_h
//-------------------------------- // timer_circulation // //--------------------------------
AN timer_circulation_pause // if not paused ... JCN TI3
L scantime // increase the timer L timer_circulation_s +R T timer_circulation_s
TI3: NOP 0 // end if
// calculate the other two timers L timer_circulation_s L 60.0 /R T timer_circulation_m
L 60.0 /R T timer_circulation_h

//--------------------------------
// Stop Command
//--------------------------------

	A	enable_stop
	A	command_stop	// stop command

	JCN	CM02

	L	0			// config: target step for this command (-1 if not used)
	T	stepnumber

CM02: NOP 0

//--------------------------------
// Hold Command
//--------------------------------

	A   enable_hold
	A(
	O 	command_hold	// hold command
	ON	hold_conditions	// hold condition active, configured outside this FB
	)
	JCN	CM03

	L	8000			// config: target step for this command (-1 if not used)
	T	stepnumber

CM03: NOP 0

//--------------------------------
// Abort Command
//--------------------------------

	A	enable_abort
	A	command_abort	// abort command
	JCN	CM05

	// Disabled in this config because we only want to allow abort when held
	L	9000			// config: target step for this command (-1 if not used)
	T	stepnumber

CM05: NOP 0

//--------------------------------
// Pause Command
//--------------------------------

	A	enable_pause
	A	command_pause	// pause command
	JCN	CM06

	L	6000			// config: target step for this command (-1 if not used)
	T	stepnumber

CM06: NOP 0

//--------------------------------
// Disable all permissions for the commands
// The individial permissions will be set again
// in the next steps depending on the state
//--------------------------------

	CLR
	=	enable_start
	=	enable_stop
	=	enable_hold
	=	enable_restart
//	=	enable_abort
	=	enable_reset
	=	enable_pause
	=	enable_resume

//--------------------------------
// 0 Idle state based on stepnumber
//--------------------------------
	L	0			// config: lowest step (-1 if state not used)
	L	stepnumber
	>=I
	L	0			// config: highest step (-1 if state not used)
	<=I     
	=	state_idle	// update state bit
	JCN ST00

	// update state as integer
	L	0
	T	state

	// enable commands
	SET
	=	enable_start

ST00: NOP 0

//--------------------------------
// 1 Running state based on stepnumber
//--------------------------------
	L	1			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	9999			// config: highest step (-1 if state not used)
	<=I 
	=	state_running	// update state bit
	JCN ST01

	// update state as integer
	L	1
	T	state

	// enable commands
	SET
	=	enable_stop
	=	enable_hold
//	=	enable_abort
	=   enable_pause

ST01: NOP 0

//--------------------------------
// 2 Completed state based on stepnumber
//--------------------------------
	L	700			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	1000			// config: highest step (-1 if state not used)
	<=I 
	=	state_complete	// update state bit
	JCN ST02

	// update state as integer
	L	2
	T	state

	// enable commands
	SET
	=	enable_reset

ST02: NOP 0

//--------------------------------
// 3 Pausing state based on stepnumber
//--------------------------------
	L	6000			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	6900			// config: highest step (-1 if state not used)
	<I 
	=	state_pausing	// update state bit
	JCN ST03

	// update state as integer
	L	3
	T	state

	// enable commands
	SET
	=	enable_stop
	=	enable_hold
//	=	enable_abort


ST03: NOP 0

//--------------------------------
// 4 Paused state based on stepnumber
//--------------------------------
	L	6900			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	6999			// config: highest step (-1 if state not used)
	<=I 
	=	state_paused	// update state bit
	JCN ST04

	// update state as integer
	L	4
	T	state

	// enable commands
	SET
	=	enable_stop
	=	enable_hold
//	=	enable_abort
	=	enable_resume

ST04: NOP 0

//--------------------------------
// 5 Holding state based on stepnumber
//--------------------------------
	L	8000			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	8900			// config: highest step (-1 if state not used)
	<I 
	=	state_holding	// update state bit
	JCN ST05

	// update state as integer
	L	5
	T	state

	// enable commands
	SET
	=	enable_stop
//	=	enable_abort

ST05: NOP 0

//--------------------------------
// 6 Held state based on stepnumber
//--------------------------------
	L	8900			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	8999			// config: highest step (-1 if state not used)
	<=I 
	=	state_held	// update state bit
	JCN ST06

	// update state as integer
	L	6
	T	state

	// enable commands
	SET
	=	enable_stop
	=	enable_restart
	=	enable_abort

ST06: NOP 0

//--------------------------------
// 7 Restarting state based on stepnumber
//--------------------------------
	L	5000			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	5900			// config: highest step (-1 if state not used)
	<=I 
	=	state_restarting	// update state bit
	JCN ST07

	// update state as integer
	L	7
	T	state

	// enable commands
	SET
	=	enable_stop
	=	enable_hold
//	=	enable_abort

ST07: NOP 0

//--------------------------------
// 8 Stopping state based on stepnumber
//--------------------------------
	L	7000			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	7900			// config: highest step (-1 if state not used)
	<I 
	=	state_stopping	// update state bit
	JCN ST08

	// update state as integer
	L	8
	T	state

	// enable commands
	SET
//	=	enable_abort

ST08: NOP 0

//--------------------------------
// 9 Stopped state based on stepnumber
//--------------------------------
	L	7900			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	7999			// config: highest step (-1 if state not used)
	<=I 
	=	state_stopped	// update state bit
	JCN ST09

	// update state as integer
	L	9
	T	state

	// enable commands
	SET
//	=	enable_abort
	=	enable_reset

ST09: NOP 0

//--------------------------------
// 10 Aborting state based on stepnumber
//--------------------------------
	L	9000			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	9900			// config: highest step (-1 if state not used)
	<I 
	=	state_aborting	// update state bit
	JCN ST10

	// update state as integer
	L	10
	T	state

ST10: NOP 0

//--------------------------------
// 11 Aborted state based on stepnumber
//--------------------------------
	L	9900			// config: lowest step (-1 if state not used)
	L	stepnumber
	<=I 
	L	9999			// config: highest step (-1 if state not used)
	<=I 
	=	state_aborted	// update state bit
	JCN ST11

	// update state as integer
	L	11
	T	state

	// enable commands
	SET
	=	enable_reset

ST11: NOP 0


//--------------------------------
// Disable start command anyway if the start_conditions are not ok
//--------------------------------

	AN	start_conditions
	R 	enable_start

// skip this network if stepnumber not equal to 0
	L 	stepnumber
	L 	0
	==I
	JCN J1

