// Step 1000
//___phase_example_Step_1000_
//================================
// function call 
//================================	  

IF (stepnumber = 1000) THEN


	IF (first_scan) THEN

		// --- FIRST SCAN ---


	ELSE

		// --- CONTINUOUS ---


		// Action: function call: write_to_DB_DW (50, 20, 123)
		write_to_DB_DW(
			DB_NO:= 50,
			Location:= 20,
			Value:= 123
		);

		// Action: function call: write_to_DB_DW (DB_NO, 20, 123)
		write_to_DB_DW(
			DB_NO:= DB_NO,
			Location:= 20,
			Value:= 123
		);

		// Action: function call: intvar = somefunction(34,32)
		intvar:= somefunction(
			arg1:= 34,
			arg2:= 32
		);

	END_IF


	// --- TRANSITION TO S0 ---
	IF (
		TRUE
	) THEN

		stepnumber:= 0;

	END_IF



	IF (stepnumber <> current_stepnumber) THEN

		// --- LAST SCAN ---


	END_IF


END_IF 






// Step 8000
//___phase_example_Step_8000_
//================================
// Holding 
//================================	  

IF (stepnumber = 8000) THEN


	IF (first_scan) THEN

		// --- FIRST SCAN ---


	ELSE

		// --- CONTINUOUS ---


	END_IF


	// --- TRANSITION TO S8990 ---
	IF (
		TRUE
	) THEN

		stepnumber:= 8990;

	END_IF



	IF (stepnumber <> current_stepnumber) THEN

		// --- LAST SCAN ---


	END_IF


END_IF 







// END OF THE STEPS AND TRANSITIONS
//################################
//################################
// Placeholder network to indicate the end of the actual sequence
//################################	  
//################################


